// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {IVotes} from "./governance/IVotes.sol";

import {IGovernor} from "./governance/IGovernor.sol";
import {GovernorSimple} from "./governance/GovernorSimple.sol";
import {GovernorCountingMajority} from "./governance/GovernorCountingMajority.sol";
import {GovernorSimpleVotes} from "./governance/GovernorSimpleVotes.sol";

/**
 * @dev Epoch based governance system that allows for a three option majority (against, for, abstain).
 *      Note that hash proposals are unique per epoch, but calls to a function with different values
 *      may be allowed any number of times. It is best to use EpochGovernor with a function that accepts
 *      no values.
 */
contract EpochGovernor is
    GovernorSimple,
    GovernorCountingMajority,
    GovernorSimpleVotes
{
    constructor(
        address _forwarder,
        IVotes _ve,
        address _splitter
    )
        GovernorSimple(_forwarder, "Epoch Governor", _splitter)
        GovernorSimpleVotes(_ve)
    {}

    function votingDelay() public pure override(IGovernor) returns (uint256) {
        return (15 minutes);
    }

    function votingPeriod() public pure override(IGovernor) returns (uint256) {
        return (1 weeks);
    }
}

contracts/factories/AutoCompounderFactor.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {IVoter} from "../interfaces/IVoter.sol";
import {IVotingEscrow} from "../interfaces/IVotingEscrow.sol";
import {IRouter} from "../interfaces/IRouter.sol";
import {IAutoCompounderFactory} from "../interfaces/factories/IAutoCompounderFactory.sol";
import {AutoCompounder} from "../AutoCompounder.sol";
import {CompoundOptimizer} from "../CompoundOptimizer.sol";

contract AutoCompounderFactory is IAutoCompounderFactory {
    /// @notice The amount rewarded per token a caller earns from calling AutoCompounder.claimXAndCompound()
    uint256 public rewardAmount = 10 * 1e18;
    uint256 internal constant MAX_REWARD_AMOUNT = 1_000 * 1e18; // 1,000 MEZO
    uint256 internal constant MIN_REWARD_AMOUNT = 1e17; // 0.1 MEZO

    address public immutable forwarder;
    address public immutable router;
    address public immutable voter;
    address public immutable optimizer;
    IVotingEscrow public immutable ve;

    mapping(address => bool) public isAutoCompounder;

    constructor(
        address _forwarder,
        address _voter,
        address _router,
        address _optimizer
    ) {
        forwarder = _forwarder;
        voter = _voter;
        router = _router;
        optimizer = _optimizer;

        ve = IVotingEscrow(IVoter(voter).ve());
    }

    function createAutoCompounder(
        address _admin,
        uint256 _tokenId
    ) external returns (address autoCompounder) {
        if (_tokenId == 0) revert TokenIdZero();
        if (!ve.isApprovedOrOwner(msg.sender, _tokenId))
            revert TokenIdNotApproved();
        if (ve.escrowType(_tokenId) != IVotingEscrow.EscrowType.MANAGED)
            revert TokenIdNotManaged();

        // create the autocompounder contract
        autoCompounder = address(
            new AutoCompounder(forwarder, router, voter, optimizer, _admin)
        );

        // transfer nft to autocompounder
        ve.safeTransferFrom(ve.ownerOf(_tokenId), autoCompounder, _tokenId);
        AutoCompounder(autoCompounder).initialize(_tokenId);

        isAutoCompounder[autoCompounder] = true;
        emit CreateAutoCompounder(msg.sender, _admin, autoCompounder);
    }

    function setRewardAmount(uint256 _rewardAmount) external {
        if (msg.sender != ve.team()) revert NotTeam();
        if (_rewardAmount == rewardAmount) revert AmountSame();
        if (
            _rewardAmount < MIN_REWARD_AMOUNT ||
            _rewardAmount > MAX_REWARD_AMOUNT
        ) revert AmountOutOfAcceptableRange();
        rewardAmount = _rewardAmount;
        emit SetRewardAmount(_rewardAmount);
    }
}

