// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.24;

import {IVotingEscrow} from "../interfaces/IVotingEscrow.sol";
import {SafeCastLibrary} from "../libraries/SafeCastLibrary.sol";
import {VotingEscrowState} from "./VotingEscrowState.sol";

library Balance {
    using SafeCastLibrary for uint256;
    using SafeCastLibrary for int128;

    uint256 internal constant WEEK = 1 weeks;

    /// @notice Binary search to get the user point index for a token id at or prior to a given timestamp
    /// @dev If a user point does not exist prior to the timestamp, this will return 0.
    /// @param _tokenId .
    /// @param _timestamp .
    /// @return User point index
    function getPastUserPointIndex(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _timestamp
    ) internal view returns (uint256) {
        uint256 _userEpoch = self.userPointEpoch[_tokenId];
        if (_userEpoch == 0) return 0;
        // First check most recent balance
        if (self._userPointHistory[_tokenId][_userEpoch].ts <= _timestamp)
            return (_userEpoch);
        // Next check implicit zero balance
        if (self._userPointHistory[_tokenId][1].ts > _timestamp) return 0;

        uint256 lower = 0;
        uint256 upper = _userEpoch;
        while (upper > lower) {
            uint256 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            IVotingEscrow.UserPoint storage userPoint = self._userPointHistory[
                _tokenId
            ][center];
            if (userPoint.ts == _timestamp) {
                return center;
            } else if (userPoint.ts < _timestamp) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return lower;
    }

    /// @notice Binary search to get the global point index at or prior to a given timestamp
    /// @dev If a checkpoint does not exist prior to the timestamp, this will return 0.
    /// @param _timestamp .
    /// @return Global point index
    function getPastGlobalPointIndex(
        VotingEscrowState.Storage storage self,
        uint256 _timestamp
    ) internal view returns (uint256) {
        if (self.epoch == 0) return 0;
        // First check most recent balance
        if (self._pointHistory[self.epoch].ts <= _timestamp)
            return (self.epoch);
        // Next check implicit zero balance
        if (self._pointHistory[1].ts > _timestamp) return 0;

        uint256 lower = 0;
        uint256 upper = self.epoch;
        while (upper > lower) {
            uint256 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            IVotingEscrow.GlobalPoint storage globalPoint = self._pointHistory[
                center
            ];
            if (globalPoint.ts == _timestamp) {
                return center;
            } else if (globalPoint.ts < _timestamp) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return lower;
    }

    /// @notice Get the current voting power for `_tokenId`
    /// @dev Adheres to the ERC20 `balanceOf` interface for Aragon compatibility
    ///      Fetches last user point prior to a certain timestamp, then walks forward to timestamp.
    /// @param _tokenId NFT for lock
    /// @param _t Epoch time to return voting power at
    /// @return User voting power
    function _balanceOfNFTAt(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _t
    ) internal view returns (uint256) {
        uint256 _epoch = getPastUserPointIndex(self, _tokenId, _t);
        // epoch 0 is an empty point
        if (_epoch == 0) return 0;
        IVotingEscrow.UserPoint memory lastPoint = self._userPointHistory[
            _tokenId
        ][_epoch];
        if (lastPoint.permanent != 0) {
            return lastPoint.permanent;
        } else {
            lastPoint.bias -= lastPoint.slope * (_t - lastPoint.ts).toInt128();
            if (lastPoint.bias < 0) {
                lastPoint.bias = 0;
            }
            return lastPoint.bias.toUint256();
        }
    }

    function _balanceOfNFT(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) external view returns (uint256) {
        if (self.ownershipChange[_tokenId] == block.number) return 0;
        return _balanceOfNFTAt(self, _tokenId, block.timestamp);
    }

    /// @notice Calculate total voting power at some point in the past
    /// @param _t Time to calculate the total voting power at
    /// @return Total voting power at that time
    function supplyAt(
        VotingEscrowState.Storage storage self,
        uint256 _t
    ) external view returns (uint256) {
        uint256 epoch_ = getPastGlobalPointIndex(self, _t);
        // epoch 0 is an empty point
        if (epoch_ == 0) return 0;
        IVotingEscrow.GlobalPoint memory _point = self._pointHistory[epoch_];
        int128 bias = _point.bias;
        int128 slope = _point.slope;
        uint256 ts = _point.ts;
        uint256 t_i = (ts / WEEK) * WEEK;
        for (uint256 i = 0; i < 255; ++i) {
            t_i += WEEK;
            int128 dSlope = 0;
            if (t_i > _t) {
                t_i = _t;
            } else {
                dSlope = self.slopeChanges[t_i];
            }
            bias -= slope * (t_i - ts).toInt128();
            if (t_i == _t) {
                break;
            }
            slope += dSlope;
            ts = t_i;
        }

        if (bias < 0) {
            bias = 0;
        }
        return bias.toUint256() + _point.permanentLockBalance;
    }
}

contracts/ve/Delegation.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.24;

import {VotingEscrowState} from "./VotingEscrowState.sol";
import {NFT} from "./NFT.sol";
import {VeERC2771Context} from "./VeERC2771Context.sol";
import {SafeCastLibrary} from "../libraries/SafeCastLibrary.sol";
import {IVotingEscrow} from "../interfaces/IVotingEscrow.sol";
import {IVotes} from "../governance/IVotes.sol";

library Delegation {
    using SafeCastLibrary for int128;
    using NFT for VotingEscrowState.Storage;
    using VeERC2771Context for VotingEscrowState.Storage;

    struct SignatureData {
        uint256 delegator;
        uint256 delegatee;
        uint256 nonce;
        uint256 expiry;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    /// @notice The EIP-712 typehash for the contract's domain
    bytes32 public constant DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,uint256 chainId,address verifyingContract)"
        );

    /// @notice The EIP-712 typehash for the delegation struct used by the contract
    bytes32 public constant DELEGATION_TYPEHASH =
        keccak256(
            "Delegation(uint256 delegator,uint256 delegatee,uint256 nonce,uint256 expiry)"
        );

    function delegate(
        VotingEscrowState.Storage storage self,
        uint256 delegator,
        uint256 delegatee
    ) external {
        if (!self._isApprovedOrOwner(self._msgSender(), delegator))
            revert IVotingEscrow.NotApprovedOrOwner();
        return _delegate(self, delegator, delegatee);
    }

    function delegateBySig(
        VotingEscrowState.Storage storage self,
        SignatureData calldata signatureData,
        string calldata contractName,
        string calldata contractVersion
    ) external {
        // EIP-2 still allows signature malleability for ecrecover(). Remove this possibility and make the signature
        // unique. Appendix F in the Ethereum Yellow paper (https://ethereum.github.io/yellowpaper/paper.pdf), defines
        // the valid range for s in (301): 0 < s < secp256k1n ÷ 2 + 1, and for v in (302): v ∈ {27, 28}. Most
        // signatures from current libraries generate a unique signature with an s-value in the lower half order.
        //
        // If your library generates malleable signatures, such as s-values in the upper range, calculate a new s-value
        // with 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEBAAEDCE6AF48A03BBFD25E8CD0364141 - s1 and flip v from 27 to 28 or
        // vice versa. If your library also generates signatures with 0/1 for v instead 27/28, add 27 to v to accept
        // these malleable signatures as well.
        if (
            uint256(signatureData.s) >
            0x7FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF5D576E7357A4501DDFE92F46681B20A0
        ) revert IVotingEscrow.InvalidSignatureS();
        bytes32 domainSeparator = keccak256(
            abi.encode(
                DOMAIN_TYPEHASH,
                keccak256(bytes(contractName)),
                keccak256(bytes(contractVersion)),
                block.chainid,
                address(this)
            )
        );
        bytes32 structHash = keccak256(
            abi.encode(
                DELEGATION_TYPEHASH,
                signatureData.delegator,
                signatureData.delegatee,
                signatureData.nonce,
                signatureData.expiry
            )
        );
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
        address signatory = ecrecover(
            digest,
            signatureData.v,
            signatureData.r,
            signatureData.s
        );
        if (!self._isApprovedOrOwner(signatory, signatureData.delegator))
            revert IVotingEscrow.NotApprovedOrOwner();
        if (signatory == address(0)) revert IVotingEscrow.InvalidSignature();
        if (signatureData.nonce != self.nonces[signatory]++)
            revert IVotingEscrow.InvalidNonce();
        if (block.timestamp > signatureData.expiry)
            revert IVotingEscrow.SignatureExpired();
        return
            _delegate(self, signatureData.delegator, signatureData.delegatee);
    }

    /// @notice Record user delegation checkpoints. Used by voting system.
    /// @dev Skips delegation if already delegated to `delegatee`.
    function _delegate(
        VotingEscrowState.Storage storage self,
        uint256 _delegator,
        uint256 _delegatee
    ) internal {
        IVotingEscrow.LockedBalance memory delegateLocked = self._locked[
            _delegator
        ];
        if (!delegateLocked.isPermanent)
            revert IVotingEscrow.NotPermanentLock();
        if (_delegatee != 0 && self._ownerOf(_delegatee) == address(0))
            revert IVotingEscrow.NonExistentToken();
        if (self.ownershipChange[_delegator] == block.number)
            revert IVotingEscrow.OwnershipChange();
        if (_delegatee == _delegator) _delegatee = 0;
        uint256 currentDelegate = self._delegates[_delegator];
        if (currentDelegate == _delegatee) return;

        uint256 delegatedBalance = delegateLocked.amount.toUint256();
        _checkpointDelegator(
            self,
            _delegator,
            _delegatee,
            self._ownerOf(_delegator)
        );
        _checkpointDelegatee(self, _delegatee, delegatedBalance, true);

        emit IVotes.DelegateChanged(
            self._msgSender(),
            currentDelegate,
            _delegatee
        );
    }

    /// @notice Used by `_mint`, `_transferFrom`, `_burn` and `delegate`
    ///         to update delegator voting checkpoints.
    ///         Automatically dedelegates, then updates checkpoint.
    /// @dev This function depends on `_locked` and must be called prior to token state changes.
    ///      If you wish to dedelegate only, use `_delegate(tokenId, 0)` instead.
    /// @param _delegator The delegator to update checkpoints for
    /// @param _delegatee The new delegatee for the delegator. Cannot be equal to `_delegator` (use 0 instead).
    /// @param _owner The new (or current) owner for the delegator
    function _checkpointDelegator(
        VotingEscrowState.Storage storage self,
        uint256 _delegator,
        uint256 _delegatee,
        address _owner
    ) internal {
        uint256 delegatedBalance = self._locked[_delegator].amount.toUint256();
        uint48 numCheckpoint = self.numCheckpoints[_delegator];
        IVotingEscrow.Checkpoint storage cpOld = numCheckpoint > 0
            ? self._checkpoints[_delegator][numCheckpoint - 1]
            : self._checkpoints[_delegator][0];
        // Dedelegate from delegatee if delegated
        _checkpointDelegatee(self, cpOld.delegatee, delegatedBalance, false);
        IVotingEscrow.Checkpoint storage cp = self._checkpoints[_delegator][
            numCheckpoint
        ];
        cp.fromTimestamp = block.timestamp;
        cp.delegatedBalance = cpOld.delegatedBalance;
        cp.delegatee = _delegatee;
        cp.owner = _owner;

        if (_isCheckpointInNewBlock(self, _delegator)) {
            self.numCheckpoints[_delegator]++;
        } else {
            self._checkpoints[_delegator][numCheckpoint - 1] = cp;
            delete self._checkpoints[_delegator][numCheckpoint];
        }

        self._delegates[_delegator] = _delegatee;
    }

    /// @notice Update delegatee's `delegatedBalance` by `balance`.
    ///         Only updates if delegating to a new delegatee.
    /// @dev If used with `balance` == `_locked[_tokenId].amount`, then this is the same as
    ///      delegating or dedelegating from `_tokenId`
    ///      If used with `balance` < `_locked[_tokenId].amount`, then this is used to adjust
    ///      `delegatedBalance` when a user's balance is modified (e.g. `increaseAmount`, `merge` etc).
    ///      If `delegatee` is 0 (i.e. user is not delegating), then do nothing.
    /// @param _delegatee The delegatee's tokenId
    /// @param balance_ The delta in balance change
    /// @param _increase True if balance is increasing, false if decreasing
    function _checkpointDelegatee(
        VotingEscrowState.Storage storage self,
        uint256 _delegatee,
        uint256 balance_,
        bool _increase
    ) internal {
        if (_delegatee == 0) return;
        uint48 numCheckpoint = self.numCheckpoints[_delegatee];
        IVotingEscrow.Checkpoint storage cpOld = numCheckpoint > 0
            ? self._checkpoints[_delegatee][numCheckpoint - 1]
            : self._checkpoints[_delegatee][0];
        IVotingEscrow.Checkpoint storage cp = self._checkpoints[_delegatee][
            numCheckpoint
        ];
        cp.fromTimestamp = block.timestamp;
        cp.owner = cpOld.owner;
        // do not expect balance_ > cpOld.delegatedBalance when decrementing but just in case
        cp.delegatedBalance = _increase
            ? cpOld.delegatedBalance + balance_
            : (
                balance_ < cpOld.delegatedBalance
                    ? cpOld.delegatedBalance - balance_
                    : 0
            );
        cp.delegatee = cpOld.delegatee;

        if (_isCheckpointInNewBlock(self, _delegatee)) {
            self.numCheckpoints[_delegatee]++;
        } else {
            self._checkpoints[_delegatee][numCheckpoint - 1] = cp;
            delete self._checkpoints[_delegatee][numCheckpoint];
        }
    }

    function _isCheckpointInNewBlock(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) internal view returns (bool) {
        uint48 _nCheckPoints = self.numCheckpoints[_tokenId];

        if (
            _nCheckPoints > 0 &&
            self._checkpoints[_tokenId][_nCheckPoints - 1].fromTimestamp ==
            block.timestamp
        ) {
            return false;
        } else {
            return true;
        }
    }

    /// @notice Retrieves historical voting balance for a token id at a given timestamp.
    /// @dev If a checkpoint does not exist prior to the timestamp, this will return 0.
    ///      The user must also own the token at the time in order to receive a voting balance.
    /// @param _account .
    /// @param _tokenId .
    /// @param _timestamp .
    /// @return Total voting balance including delegations at a given timestamp.
    function getPastVotes(
        VotingEscrowState.Storage storage self,
        address _account,
        uint256 _tokenId,
        uint256 _timestamp
    ) external view returns (uint256) {
        uint48 _checkIndex = getPastVotesIndex(self, _tokenId, _timestamp);
        IVotingEscrow.Checkpoint memory lastCheckpoint = self._checkpoints[
            _tokenId
        ][_checkIndex];
        // If no point exists prior to the given timestamp, return 0
        if (lastCheckpoint.fromTimestamp > _timestamp) return 0;
        // Check ownership
        if (_account != lastCheckpoint.owner) return 0;
        uint256 votes = lastCheckpoint.delegatedBalance;
        return
            lastCheckpoint.delegatee == 0
                ? votes +
                    IVotingEscrow(address(this)).balanceOfNFTAt(
                        _tokenId,
                        _timestamp
                    )
                : votes;
    }

    /// @notice Binary search to get the voting checkpoint for a token id at or prior to a given timestamp.
    /// @dev If a checkpoint does not exist prior to the timestamp, this will return 0.
    /// @param _tokenId .
    /// @param _timestamp .
    /// @return The index of the checkpoint.
    function getPastVotesIndex(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _timestamp
    ) internal view returns (uint48) {
        uint48 nCheckpoints = self.numCheckpoints[_tokenId];
        if (nCheckpoints == 0) return 0;
        // First check most recent balance
        if (
            self._checkpoints[_tokenId][nCheckpoints - 1].fromTimestamp <=
            _timestamp
        ) return (nCheckpoints - 1);
        // Next check implicit zero balance
        if (self._checkpoints[_tokenId][0].fromTimestamp > _timestamp) return 0;

        uint48 lower = 0;
        uint48 upper = nCheckpoints - 1;
        while (upper > lower) {
            uint48 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            IVotingEscrow.Checkpoint storage cp = self._checkpoints[_tokenId][
                center
            ];
            if (cp.fromTimestamp == _timestamp) {
                return center;
            } else if (cp.fromTimestamp < _timestamp) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return lower;
    }
}

contracts/ve/Escrow.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.24;

import {VotingEscrowState} from "./VotingEscrowState.sol";
import {Delegation} from "./Delegation.sol";
import {NFT} from "./NFT.sol";
import {VeERC2771Context} from "./VeERC2771Context.sol";
import {SafeCastLibrary} from "../libraries/SafeCastLibrary.sol";
import {IReward} from "../interfaces/IReward.sol";
import {IVotingEscrow} from "../interfaces/IVotingEscrow.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC4906} from "@openzeppelin/contracts/interfaces/IERC4906.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

library Escrow {
    using SafeERC20 for IERC20;
    using SafeCastLibrary for uint256;
    using SafeCastLibrary for int128;
    using NFT for VotingEscrowState.Storage;
    using Delegation for VotingEscrowState.Storage;
    using VeERC2771Context for VotingEscrowState.Storage;

    uint256 internal constant WEEK = 1 weeks;
    uint256 internal constant MAXTIME = 4 * 365 * 86400;
    int128 internal constant iMAXTIME = 4 * 365 * 86400;
    uint256 internal constant MULTIPLIER = 1 ether;

    function depositFor(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _value
    ) external {
        if (
            self.escrowType[_tokenId] == IVotingEscrow.EscrowType.MANAGED &&
            self._msgSender() != self.distributor
        ) revert IVotingEscrow.NotDistributor();
        _increaseAmountFor(
            self,
            _tokenId,
            _value,
            IVotingEscrow.DepositType.DEPOSIT_FOR_TYPE
        );
    }

    /// @notice Deposit and lock tokens for a user
    /// @param _tokenId NFT that holds lock
    /// @param _value Amount to deposit
    /// @param _unlockTime New time when to unlock the tokens, or 0 if unchanged
    /// @param _oldLocked Previous locked amount / timestamp
    /// @param _depositType The type of deposit
    function _depositFor(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _value,
        uint256 _unlockTime,
        IVotingEscrow.LockedBalance memory _oldLocked,
        IVotingEscrow.DepositType _depositType
    ) internal {
        uint256 supplyBefore = self.supply;
        self.supply = supplyBefore + _value;

        // Set newLocked to _oldLocked without mangling memory
        IVotingEscrow.LockedBalance memory newLocked;
        (newLocked.amount, newLocked.end, newLocked.isPermanent) = (
            _oldLocked.amount,
            _oldLocked.end,
            _oldLocked.isPermanent
        );

        // Adding to existing lock, or if a lock is expired - creating a new one
        newLocked.amount += _value.toInt128();
        if (_unlockTime != 0) {
            newLocked.end = _unlockTime;
        }
        self._locked[_tokenId] = newLocked;

        // Possibilities:
        // Both _oldLocked.end could be current or expired (>/< block.timestamp)
        // or if the lock is a permanent lock, then _oldLocked.end == 0
        // value == 0 (extend lock) or value > 0 (add to lock or extend lock)
        // newLocked.end > block.timestamp (always)
        _checkpoint(self, _tokenId, _oldLocked, newLocked);

        address from = self._msgSender();
        if (_value != 0) {
            IERC20(self.token).safeTransferFrom(from, address(this), _value);
        }

        emit IVotingEscrow.Deposit(
            from,
            _tokenId,
            _depositType,
            _value,
            newLocked.end,
            block.timestamp
        );
        emit IVotingEscrow.Supply(supplyBefore, supplyBefore + _value);
    }

    /// @notice Record global and per-user data to checkpoints. Used by VotingEscrow system.
    /// @param _tokenId NFT token ID. No user checkpoint if 0
    /// @param _oldLocked Previous locked amount / end lock time for the user
    /// @param _newLocked New locked amount / end lock time for the user
    function _checkpoint(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        IVotingEscrow.LockedBalance memory _oldLocked,
        IVotingEscrow.LockedBalance memory _newLocked
    ) internal {
        IVotingEscrow.UserPoint memory uOld;
        IVotingEscrow.UserPoint memory uNew;
        int128 oldDslope = 0;
        int128 newDslope = 0;
        uint256 _epoch = self.epoch;

        if (_tokenId != 0) {
            uNew.permanent = _newLocked.isPermanent
                ? _newLocked.amount.toUint256()
                : 0;
            // Calculate slopes and biases
            // Kept at zero when they have to
            if (_oldLocked.end > block.timestamp && _oldLocked.amount > 0) {
                uOld.slope = _oldLocked.amount / iMAXTIME;
                uOld.bias =
                    uOld.slope *
                    (_oldLocked.end - block.timestamp).toInt128();
            }
            if (_newLocked.end > block.timestamp && _newLocked.amount > 0) {
                uNew.slope = _newLocked.amount / iMAXTIME;
                uNew.bias =
                    uNew.slope *
                    (_newLocked.end - block.timestamp).toInt128();
            }

            // Read values of scheduled changes in the slope
            // _oldLocked.end can be in the past and in the future
            // _newLocked.end can ONLY by in the FUTURE unless everything expired: than zeros
            oldDslope = self.slopeChanges[_oldLocked.end];
            if (_newLocked.end != 0) {
                if (_newLocked.end == _oldLocked.end) {
                    newDslope = oldDslope;
                } else {
                    newDslope = self.slopeChanges[_newLocked.end];
                }
            }
        }

        IVotingEscrow.GlobalPoint memory lastPoint = IVotingEscrow.GlobalPoint({
            bias: 0,
            slope: 0,
            ts: block.timestamp,
            blk: block.number,
            permanentLockBalance: 0
        });
        if (_epoch > 0) {
            lastPoint = self._pointHistory[_epoch];
        }
        uint256 lastCheckpoint = lastPoint.ts;
        // initialLastPoint is used for extrapolation to calculate block number
        // (approximately, for *At methods) and save them
        // as we cannot figure that out exactly from inside the contract
        IVotingEscrow.GlobalPoint memory initialLastPoint = IVotingEscrow
            .GlobalPoint({
                bias: lastPoint.bias,
                slope: lastPoint.slope,
                ts: lastPoint.ts,
                blk: lastPoint.blk,
                permanentLockBalance: lastPoint.permanentLockBalance
            });
        uint256 blockSlope = 0; // dblock/dt
        if (block.timestamp > lastPoint.ts) {
            blockSlope =
                (MULTIPLIER * (block.number - lastPoint.blk)) /
                (block.timestamp - lastPoint.ts);
        }
        // If last point is already recorded in this block, slope=0
        // But that's ok b/c we know the block in such case

        // Go over weeks to fill history and calculate what the current point is
        (_epoch, lastPoint) = _updateHistory(
            self,
            _epoch,
            lastPoint,
            lastCheckpoint,
            initialLastPoint,
            blockSlope
        );

        if (_tokenId != 0) {
            // If last point was in this block, the slope change has been applied already
            // But in such case we have 0 slope(s)
            lastPoint.slope += (uNew.slope - uOld.slope);
            lastPoint.bias += (uNew.bias - uOld.bias);
            if (lastPoint.slope < 0) {
                lastPoint.slope = 0;
            }
            if (lastPoint.bias < 0) {
                lastPoint.bias = 0;
            }
            lastPoint.permanentLockBalance = self.permanentLockBalance;
        }

        // If timestamp of last global point is the same, overwrite the last global point
        // Else record the new global point into history
        // Exclude epoch 0 (note: _epoch is always >= 1, see above)
        // Two possible outcomes:
        // Missing global checkpoints in prior weeks. In this case, _epoch = epoch + x, where x > 1
        // No missing global checkpoints, but timestamp != block.timestamp. Create new checkpoint.
        // No missing global checkpoints, but timestamp == block.timestamp. Overwrite last checkpoint.
        if (
            _epoch != 1 && self._pointHistory[_epoch - 1].ts == block.timestamp
        ) {
            // _epoch = epoch + 1, so we do not increment epoch
            self._pointHistory[_epoch - 1] = lastPoint;
        } else {
            // more than one global point may have been written, so we update epoch
            self.epoch = _epoch;
            self._pointHistory[_epoch] = lastPoint;
        }

        if (_tokenId != 0) {
            // Schedule the slope changes (slope is going down)
            // We subtract new_user_slope from [_newLocked.end]
            // and add old_user_slope to [_oldLocked.end]
            if (_oldLocked.end > block.timestamp) {
                // oldDslope was <something> - uOld.slope, so we cancel that
                oldDslope += uOld.slope;
                if (_newLocked.end == _oldLocked.end) {
                    oldDslope -= uNew.slope; // It was a new deposit, not extension
                }
                self.slopeChanges[_oldLocked.end] = oldDslope;
            }

            if (_newLocked.end > block.timestamp) {
                // update slope if new lock is greater than old lock and is not permanent or if old lock is permanent
                if ((_newLocked.end > _oldLocked.end)) {
                    newDslope -= uNew.slope; // old slope disappeared at this point
                    self.slopeChanges[_newLocked.end] = newDslope;
                }
                // else: we recorded it already in oldDslope
            }
            // If timestamp of last user point is the same, overwrite the last user point
            // Else record the new user point into history
            // Exclude epoch 0
            uNew.ts = block.timestamp;
            uNew.blk = block.number;
            uint256 userEpoch = self.userPointEpoch[_tokenId];
            if (
                userEpoch != 0 &&
                self._userPointHistory[_tokenId][userEpoch].ts ==
                block.timestamp
            ) {
                self._userPointHistory[_tokenId][userEpoch] = uNew;
            } else {
                self.userPointEpoch[_tokenId] = ++userEpoch;
                self._userPointHistory[_tokenId][userEpoch] = uNew;
            }
        }
    }

    function _updateHistory(
        VotingEscrowState.Storage storage self,
        uint256 _epoch,
        IVotingEscrow.GlobalPoint memory lastPoint,
        uint256 lastCheckpoint,
        IVotingEscrow.GlobalPoint memory initialLastPoint,
        uint256 blockSlope
    ) internal returns (uint256, IVotingEscrow.GlobalPoint memory) {
        uint256 t_i = (lastCheckpoint / WEEK) * WEEK;
        for (uint256 i = 0; i < 255; ++i) {
            // Hopefully it won't happen that this won't get used in 5 years!
            // If it does, users will be able to withdraw but vote weight will be broken
            t_i += WEEK; // Initial value of t_i is always larger than the ts of the last point
            int128 d_slope = 0;
            if (t_i > block.timestamp) {
                t_i = block.timestamp;
            } else {
                d_slope = self.slopeChanges[t_i];
            }
            lastPoint.bias -=
                lastPoint.slope *
                (t_i - lastCheckpoint).toInt128();
            lastPoint.slope += d_slope;
            if (lastPoint.bias < 0) {
                // This can happen
                lastPoint.bias = 0;
            }
            if (lastPoint.slope < 0) {
                // This cannot happen - just in case
                lastPoint.slope = 0;
            }
            lastCheckpoint = t_i;
            lastPoint.ts = t_i;
            lastPoint.blk =
                initialLastPoint.blk +
                (blockSlope * (t_i - initialLastPoint.ts)) /
                MULTIPLIER;
            _epoch += 1;
            if (t_i == block.timestamp) {
                lastPoint.blk = block.number;
                break;
            } else {
                self._pointHistory[_epoch] = lastPoint;
            }
        }
        return (_epoch, lastPoint);
    }

    /// @dev Deposit `_value` tokens for `_to` and lock for `_lockDuration`
    /// @param _value Amount to deposit
    /// @param _lockDuration Number of seconds to lock tokens for (rounded down to nearest week)
    /// @param _to Address to deposit
    function _createLock(
        VotingEscrowState.Storage storage self,
        uint256 _value,
        uint256 _lockDuration,
        address _to
    ) external returns (uint256) {
        uint256 unlockTime = ((block.timestamp + _lockDuration) / WEEK) * WEEK; // Locktime is rounded down to weeks

        if (_value == 0) revert IVotingEscrow.ZeroAmount();
        if (unlockTime <= block.timestamp)
            revert IVotingEscrow.LockDurationNotInFuture();
        if (unlockTime > block.timestamp + MAXTIME)
            revert IVotingEscrow.LockDurationTooLong();

        uint256 _tokenId = ++self.tokenId;
        self._mint(_to, _tokenId);

        _depositFor(
            self,
            _tokenId,
            _value,
            unlockTime,
            self._locked[_tokenId],
            IVotingEscrow.DepositType.CREATE_LOCK_TYPE
        );
        return _tokenId;
    }

    function increaseAmount(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _value
    ) external {
        if (!self._isApprovedOrOwner(self._msgSender(), _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        _increaseAmountFor(
            self,
            _tokenId,
            _value,
            IVotingEscrow.DepositType.INCREASE_LOCK_AMOUNT
        );
    }

    function _increaseAmountFor(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _value,
        IVotingEscrow.DepositType _depositType
    ) internal {
        IVotingEscrow.EscrowType _escrowType = self.escrowType[_tokenId];
        if (_escrowType == IVotingEscrow.EscrowType.LOCKED)
            revert IVotingEscrow.NotManagedOrNormalNFT();

        IVotingEscrow.LockedBalance memory oldLocked = self._locked[_tokenId];

        if (_value == 0) revert IVotingEscrow.ZeroAmount();
        if (oldLocked.amount <= 0) revert IVotingEscrow.NoLockFound();
        if (oldLocked.end <= block.timestamp && !oldLocked.isPermanent)
            revert IVotingEscrow.LockExpired();

        if (oldLocked.isPermanent) self.permanentLockBalance += _value;
        self._checkpointDelegatee(self._delegates[_tokenId], _value, true);
        _depositFor(self, _tokenId, _value, 0, oldLocked, _depositType);

        if (_escrowType == IVotingEscrow.EscrowType.MANAGED) {
            // increaseAmount called on managed tokens are treated as locked rewards
            address _lockedManagedReward = self.managedToLocked[_tokenId];
            address _token = self.token;
            IERC20(_token).safeApprove(_lockedManagedReward, _value);
            IReward(_lockedManagedReward).notifyRewardAmount(_token, _value);
            IERC20(_token).safeApprove(_lockedManagedReward, 0);
        }

        emit IERC4906.MetadataUpdate(_tokenId);
    }

    function increaseUnlockTime(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _lockDuration
    ) external {
        if (!self._isApprovedOrOwner(self._msgSender(), _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        if (self.escrowType[_tokenId] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();

        IVotingEscrow.LockedBalance memory oldLocked = self._locked[_tokenId];
        if (oldLocked.isPermanent) revert IVotingEscrow.PermanentLock();
        uint256 unlockTime = ((block.timestamp + _lockDuration) / WEEK) * WEEK; // Locktime is rounded down to weeks

        if (oldLocked.end <= block.timestamp)
            revert IVotingEscrow.LockExpired();
        if (oldLocked.amount <= 0) revert IVotingEscrow.NoLockFound();
        if (unlockTime <= oldLocked.end)
            revert IVotingEscrow.LockDurationNotInFuture();
        if (unlockTime > block.timestamp + MAXTIME)
            revert IVotingEscrow.LockDurationTooLong();

        _depositFor(
            self,
            _tokenId,
            0,
            unlockTime,
            oldLocked,
            IVotingEscrow.DepositType.INCREASE_UNLOCK_TIME
        );

        emit IERC4906.MetadataUpdate(_tokenId);
    }

    function withdraw(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) external {
        address sender = self._msgSender();
        if (!self._isApprovedOrOwner(sender, _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        if (self.voted[_tokenId]) revert IVotingEscrow.AlreadyVoted();
        if (self.escrowType[_tokenId] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();

        IVotingEscrow.LockedBalance memory oldLocked = self._locked[_tokenId];
        if (oldLocked.isPermanent) revert IVotingEscrow.PermanentLock();
        if (block.timestamp < oldLocked.end)
            revert IVotingEscrow.LockNotExpired();
        uint256 value = oldLocked.amount.toUint256();

        // Burn the NFT
        self._burn(_tokenId);
        self._locked[_tokenId] = IVotingEscrow.LockedBalance(0, 0, false);
        uint256 supplyBefore = self.supply;
        self.supply = supplyBefore - value;

        // oldLocked can have either expired <= timestamp or zero end
        // oldLocked has only 0 end
        // Both can have >= 0 amount
        _checkpoint(
            self,
            _tokenId,
            oldLocked,
            IVotingEscrow.LockedBalance(0, 0, false)
        );

        IERC20(self.token).safeTransfer(sender, value);

        emit IVotingEscrow.Withdraw(sender, _tokenId, value, block.timestamp);
        emit IVotingEscrow.Supply(supplyBefore, supplyBefore - value);
    }

    function merge(
        VotingEscrowState.Storage storage self,
        uint256 _from,
        uint256 _to
    ) external {
        address sender = self._msgSender();
        if (self.voted[_from]) revert IVotingEscrow.AlreadyVoted();
        if (self.escrowType[_from] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();
        if (self.escrowType[_to] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();
        if (_from == _to) revert IVotingEscrow.SameNFT();
        if (!self._isApprovedOrOwner(sender, _from))
            revert IVotingEscrow.NotApprovedOrOwner();
        if (!self._isApprovedOrOwner(sender, _to))
            revert IVotingEscrow.NotApprovedOrOwner();
        IVotingEscrow.LockedBalance memory oldLockedTo = self._locked[_to];
        if (oldLockedTo.end <= block.timestamp && !oldLockedTo.isPermanent)
            revert IVotingEscrow.LockExpired();

        IVotingEscrow.LockedBalance memory oldLockedFrom = self._locked[_from];
        if (oldLockedFrom.isPermanent) revert IVotingEscrow.PermanentLock();
        uint256 end = oldLockedFrom.end >= oldLockedTo.end
            ? oldLockedFrom.end
            : oldLockedTo.end;

        self._burn(_from);
        self._locked[_from] = IVotingEscrow.LockedBalance(0, 0, false);
        _checkpoint(
            self,
            _from,
            oldLockedFrom,
            IVotingEscrow.LockedBalance(0, 0, false)
        );

        IVotingEscrow.LockedBalance memory newLockedTo;
        newLockedTo.amount = oldLockedTo.amount + oldLockedFrom.amount;
        newLockedTo.isPermanent = oldLockedTo.isPermanent;
        if (newLockedTo.isPermanent) {
            self.permanentLockBalance += oldLockedFrom.amount.toUint256();
        } else {
            newLockedTo.end = end;
        }
        self._checkpointDelegatee(
            self._delegates[_to],
            oldLockedFrom.amount.toUint256(),
            true
        );
        _checkpoint(self, _to, oldLockedTo, newLockedTo);
        self._locked[_to] = newLockedTo;

        emit IVotingEscrow.Merge(
            sender,
            _from,
            _to,
            oldLockedFrom.amount.toUint256(),
            oldLockedTo.amount.toUint256(),
            newLockedTo.amount.toUint256(),
            newLockedTo.end,
            block.timestamp
        );
        emit IERC4906.MetadataUpdate(_to);
    }

    function split(
        VotingEscrowState.Storage storage self,
        uint256 _from,
        uint256 _amount
    ) external returns (uint256 _tokenId1, uint256 _tokenId2) {
        address sender = self._msgSender();
        address owner = self._ownerOf(_from);
        if (owner == address(0)) revert IVotingEscrow.SplitNoOwner();
        if (!self.canSplit[owner] && !self.canSplit[address(0)])
            revert IVotingEscrow.SplitNotAllowed();
        if (self.escrowType[_from] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();
        if (self.voted[_from]) revert IVotingEscrow.AlreadyVoted();
        if (!self._isApprovedOrOwner(sender, _from))
            revert IVotingEscrow.NotApprovedOrOwner();
        IVotingEscrow.LockedBalance memory newLocked = self._locked[_from];
        if (newLocked.end <= block.timestamp && !newLocked.isPermanent)
            revert IVotingEscrow.LockExpired();
        int128 _splitAmount = _amount.toInt128();
        if (_splitAmount == 0) revert IVotingEscrow.ZeroAmount();
        if (newLocked.amount <= _splitAmount)
            revert IVotingEscrow.AmountTooBig();

        // Zero out and burn old veNFT
        self._burn(_from);
        self._locked[_from] = IVotingEscrow.LockedBalance(0, 0, false);
        _checkpoint(
            self,
            _from,
            newLocked,
            IVotingEscrow.LockedBalance(0, 0, false)
        );

        // Create new veNFT using old balance - amount
        newLocked.amount -= _splitAmount;
        _tokenId1 = _createSplitNFT(self, owner, newLocked);

        // Create new veNFT using amount
        newLocked.amount = _splitAmount;
        _tokenId2 = _createSplitNFT(self, owner, newLocked);

        emit IVotingEscrow.Split(
            _from,
            _tokenId1,
            _tokenId2,
            sender,
            self._locked[_tokenId1].amount.toUint256(),
            _splitAmount.toUint256(),
            newLocked.end,
            block.timestamp
        );
    }

    function _createSplitNFT(
        VotingEscrowState.Storage storage self,
        address _to,
        IVotingEscrow.LockedBalance memory _newLocked
    ) internal returns (uint256 _tokenId) {
        _tokenId = ++self.tokenId;
        self._locked[_tokenId] = _newLocked;
        _checkpoint(
            self,
            _tokenId,
            IVotingEscrow.LockedBalance(0, 0, false),
            _newLocked
        );
        self._mint(_to, _tokenId);
    }

    function toggleSplit(
        VotingEscrowState.Storage storage self,
        address _account,
        bool _bool
    ) external {
        if (self._msgSender() != self.team) revert IVotingEscrow.NotTeam();
        self.canSplit[_account] = _bool;
    }

    function lockPermanent(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) external {
        address sender = self._msgSender();
        if (!self._isApprovedOrOwner(sender, _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        if (self.escrowType[_tokenId] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();
        IVotingEscrow.LockedBalance memory _newLocked = self._locked[_tokenId];
        if (_newLocked.isPermanent) revert IVotingEscrow.PermanentLock();
        if (_newLocked.end <= block.timestamp)
            revert IVotingEscrow.LockExpired();
        if (_newLocked.amount <= 0) revert IVotingEscrow.NoLockFound();

        uint256 _amount = _newLocked.amount.toUint256();
        self.permanentLockBalance += _amount;
        _newLocked.end = 0;
        _newLocked.isPermanent = true;
        _checkpoint(self, _tokenId, self._locked[_tokenId], _newLocked);
        self._locked[_tokenId] = _newLocked;

        emit IVotingEscrow.LockPermanent(
            sender,
            _tokenId,
            _amount,
            block.timestamp
        );
        emit IERC4906.MetadataUpdate(_tokenId);
    }

    function unlockPermanent(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) external {
        address sender = self._msgSender();
        if (!self._isApprovedOrOwner(sender, _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        if (self.escrowType[_tokenId] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();
        if (self.voted[_tokenId]) revert IVotingEscrow.AlreadyVoted();
        IVotingEscrow.LockedBalance memory _newLocked = self._locked[_tokenId];
        if (!_newLocked.isPermanent) revert IVotingEscrow.NotPermanentLock();

        uint256 _amount = _newLocked.amount.toUint256();
        self.permanentLockBalance -= _amount;
        _newLocked.end = ((block.timestamp + MAXTIME) / WEEK) * WEEK;
        _newLocked.isPermanent = false;
        self._delegate(_tokenId, 0);
        _checkpoint(self, _tokenId, self._locked[_tokenId], _newLocked);
        self._locked[_tokenId] = _newLocked;

        emit IVotingEscrow.UnlockPermanent(
            sender,
            _tokenId,
            _amount,
            block.timestamp
        );
        emit IERC4906.MetadataUpdate(_tokenId);
    }
}

contracts/ve/ManagedNFT.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.24;

import {VotingEscrowState} from "./VotingEscrowState.sol";
import {Escrow} from "./Escrow.sol";
import {NFT} from "./NFT.sol";
import {Delegation} from "./Delegation.sol";
import {Balance} from "./Balance.sol";
import {VeERC2771Context} from "./VeERC2771Context.sol";
import {SafeCastLibrary} from "../libraries/SafeCastLibrary.sol";
import {IVotingEscrow} from "../interfaces/IVotingEscrow.sol";
import {IReward} from "../interfaces/IReward.sol";
import {IVoter} from "../interfaces/IVoter.sol";
import {IManagedRewardsFactory} from "../interfaces/factories/IManagedRewardsFactory.sol";
import {IFactoryRegistry} from "../interfaces/factories/IFactoryRegistry.sol";
import {IERC4906} from "@openzeppelin/contracts/interfaces/IERC4906.sol";

library ManagedNFT {
    using SafeCastLibrary for uint256;
    using SafeCastLibrary for int128;
    using NFT for VotingEscrowState.Storage;
    using Escrow for VotingEscrowState.Storage;
    using Delegation for VotingEscrowState.Storage;
    using Balance for VotingEscrowState.Storage;
    using VeERC2771Context for VotingEscrowState.Storage;

    function createManagedLockFor(
        VotingEscrowState.Storage storage self,
        address _to
    ) external returns (uint256 _mTokenId) {
        address sender = self._msgSender();
        if (
            sender != self.allowedManager &&
            sender != IVoter(self.voter).governor()
        ) revert IVotingEscrow.NotGovernorOrManager();

        _mTokenId = ++self.tokenId;
        self._mint(_to, _mTokenId);
        self._depositFor(
            _mTokenId,
            0,
            0,
            IVotingEscrow.LockedBalance(0, 0, true),
            IVotingEscrow.DepositType.CREATE_LOCK_TYPE
        );

        self.escrowType[_mTokenId] = IVotingEscrow.EscrowType.MANAGED;

        (
            address _lockedManagedReward,
            address _freeManagedReward
        ) = IManagedRewardsFactory(
                IFactoryRegistry(self.factoryRegistry).managedRewardsFactory()
            ).createRewards(self.trustedForwarder, self.voter);
        self.managedToLocked[_mTokenId] = _lockedManagedReward;
        self.managedToFree[_mTokenId] = _freeManagedReward;

        emit IVotingEscrow.CreateManaged(
            _to,
            _mTokenId,
            sender,
            _lockedManagedReward,
            _freeManagedReward
        );
    }

    function depositManaged(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _mTokenId
    ) external {
        if (self._msgSender() != self.voter) revert IVotingEscrow.NotVoter();
        if (self.escrowType[_mTokenId] != IVotingEscrow.EscrowType.MANAGED)
            revert IVotingEscrow.NotManagedNFT();
        if (self.escrowType[_tokenId] != IVotingEscrow.EscrowType.NORMAL)
            revert IVotingEscrow.NotNormalNFT();
        if (self._balanceOfNFTAt(_tokenId, block.timestamp) == 0)
            revert IVotingEscrow.ZeroBalance();

        // adjust user nft
        int128 _amount = self._locked[_tokenId].amount;
        if (self._locked[_tokenId].isPermanent) {
            self.permanentLockBalance -= _amount.toUint256();
            self._delegate(_tokenId, 0);
        }
        self._checkpoint(
            _tokenId,
            self._locked[_tokenId],
            IVotingEscrow.LockedBalance(0, 0, false)
        );
        self._locked[_tokenId] = IVotingEscrow.LockedBalance(0, 0, false);

        // adjust managed nft
        uint256 _weight = _amount.toUint256();
        self.permanentLockBalance += _weight;
        IVotingEscrow.LockedBalance memory newLocked = self._locked[_mTokenId];
        newLocked.amount += _amount;
        self._checkpointDelegatee(self._delegates[_mTokenId], _weight, true);
        self._checkpoint(_mTokenId, self._locked[_mTokenId], newLocked);
        self._locked[_mTokenId] = newLocked;

        self.weights[_tokenId][_mTokenId] = _weight;
        self.idToManaged[_tokenId] = _mTokenId;
        self.escrowType[_tokenId] = IVotingEscrow.EscrowType.LOCKED;

        address _lockedManagedReward = self.managedToLocked[_mTokenId];
        IReward(_lockedManagedReward)._deposit(_weight, _tokenId);
        address _freeManagedReward = self.managedToFree[_mTokenId];
        IReward(_freeManagedReward)._deposit(_weight, _tokenId);

        emit IVotingEscrow.DepositManaged(
            self._ownerOf(_tokenId),
            _tokenId,
            _mTokenId,
            _weight,
            block.timestamp
        );
        emit IERC4906.MetadataUpdate(_tokenId);
    }

    function withdrawManaged(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) external {
        uint256 _mTokenId = self.idToManaged[_tokenId];
        if (self._msgSender() != self.voter) revert IVotingEscrow.NotVoter();
        if (_mTokenId == 0) revert IVotingEscrow.InvalidManagedNFTId();
        if (self.escrowType[_tokenId] != IVotingEscrow.EscrowType.LOCKED)
            revert IVotingEscrow.NotLockedNFT();

        // update accrued rewards
        address _lockedManagedReward = self.managedToLocked[_mTokenId];
        address _freeManagedReward = self.managedToFree[_mTokenId];
        uint256 _weight = self.weights[_tokenId][_mTokenId];
        uint256 _reward = IReward(_lockedManagedReward).earned(
            address(self.token),
            _tokenId
        );
        uint256 _total = _weight + _reward;
        uint256 _unlockTime = ((block.timestamp + Escrow.MAXTIME) /
            Escrow.WEEK) * Escrow.WEEK;

        // claim locked rewards (rebases + compounded reward)
        address[] memory rewards = new address[](1);
        rewards[0] = address(self.token);
        IReward(_lockedManagedReward).getReward(_tokenId, rewards);

        _adjustUserNFT(self, _tokenId, _total, _unlockTime);
        _adjustManagedNFT(self, _mTokenId, _total);

        IReward(_lockedManagedReward)._withdraw(_weight, _tokenId);
        IReward(_freeManagedReward)._withdraw(_weight, _tokenId);

        delete self.idToManaged[_tokenId];
        delete self.weights[_tokenId][_mTokenId];
        delete self.escrowType[_tokenId];

        emit IVotingEscrow.WithdrawManaged(
            self._ownerOf(_tokenId),
            _tokenId,
            _mTokenId,
            _total,
            block.timestamp
        );
        emit IERC4906.MetadataUpdate(_tokenId);
    }

    function _adjustUserNFT(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId,
        uint256 _total,
        uint256 _unlockTime
    ) internal {
        IVotingEscrow.LockedBalance memory newLockedNormal = IVotingEscrow
            .LockedBalance(_total.toInt128(), _unlockTime, false);
        self._checkpoint(_tokenId, self._locked[_tokenId], newLockedNormal);
        self._locked[_tokenId] = newLockedNormal;
    }

    function _adjustManagedNFT(
        VotingEscrowState.Storage storage self,
        uint256 _mTokenId,
        uint256 _total
    ) internal {
        IVotingEscrow.LockedBalance memory newLockedManaged = self._locked[
            _mTokenId
        ];
        // do not expect _total > locked.amount / permanentLockBalance but just in case
        newLockedManaged.amount -= (
            _total.toInt128() < newLockedManaged.amount
                ? _total.toInt128()
                : newLockedManaged.amount
        );
        self.permanentLockBalance -= (
            _total < self.permanentLockBalance
                ? _total
                : self.permanentLockBalance
        );
        self._checkpointDelegatee(self._delegates[_mTokenId], _total, false);
        self._checkpoint(_mTokenId, self._locked[_mTokenId], newLockedManaged);
        self._locked[_mTokenId] = newLockedManaged;
    }

    function setAllowedManager(
        VotingEscrowState.Storage storage self,
        address _allowedManager
    ) external {
        if (self._msgSender() != IVoter(self.voter).governor())
            revert IVotingEscrow.NotGovernor();
        if (_allowedManager == self.allowedManager)
            revert IVotingEscrow.SameAddress();
        if (_allowedManager == address(0)) revert IVotingEscrow.ZeroAddress();
        self.allowedManager = _allowedManager;
        emit IVotingEscrow.SetAllowedManager(_allowedManager);
    }

    function setManagedState(
        VotingEscrowState.Storage storage self,
        uint256 _mTokenId,
        bool _state
    ) external {
        if (
            self._msgSender() != IVoter(self.voter).emergencyCouncil() &&
            self._msgSender() != IVoter(self.voter).governor()
        ) revert IVotingEscrow.NotEmergencyCouncilOrGovernor();
        if (self.escrowType[_mTokenId] != IVotingEscrow.EscrowType.MANAGED)
            revert IVotingEscrow.NotManagedNFT();
        if (self.deactivated[_mTokenId] == _state)
            revert IVotingEscrow.SameState();
        self.deactivated[_mTokenId] = _state;
    }
}

contracts/ve/NFT.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.24;

import {VotingEscrowState} from "./VotingEscrowState.sol";
import {Delegation} from "./Delegation.sol";
import {VeERC2771Context} from "./VeERC2771Context.sol";
import {IVotingEscrow} from "../interfaces/IVotingEscrow.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

library NFT {
    using Delegation for VotingEscrowState.Storage;
    using VeERC2771Context for VotingEscrowState.Storage;

    function approve(
        VotingEscrowState.Storage storage self,
        address _approved,
        uint256 _tokenId
    ) external {
        address sender = self._msgSender();
        address owner = _ownerOf(self, _tokenId);
        // Throws if `_tokenId` is not a valid NFT
        if (owner == address(0)) revert IVotingEscrow.ZeroAddress();
        // Throws if `_approved` is the current owner
        if (owner == _approved) revert IVotingEscrow.SameAddress();
        // Check requirements
        bool senderIsOwner = (_ownerOf(self, _tokenId) == sender);
        bool senderIsApprovedForAll = (self.ownerToOperators[owner])[sender];
        if (!senderIsOwner && !senderIsApprovedForAll)
            revert IVotingEscrow.NotApprovedOrOwner();
        // Set the approval
        self.idToApprovals[_tokenId] = _approved;
        emit IERC721.Approval(owner, _approved, _tokenId);
    }

    function setApprovalForAll(
        VotingEscrowState.Storage storage self,
        address _operator,
        bool _approved
    ) external {
        address sender = self._msgSender();
        // Throws if `_operator` is the `msg.sender`
        if (_operator == sender) revert IVotingEscrow.SameAddress();
        self.ownerToOperators[sender][_operator] = _approved;
        emit IERC721.ApprovalForAll(sender, _operator, _approved);
    }

    function safeTransferFrom(
        VotingEscrowState.Storage storage self,
        address _from,
        address _to,
        uint256 _tokenId,
        bytes memory _data
    ) external {
        address sender = self._msgSender();
        _transferFrom(self, _from, _to, _tokenId, sender);

        if (_isContract(_to)) {
            // Throws if transfer destination is a contract which does not implement 'onERC721Received'
            try
                IERC721Receiver(_to).onERC721Received(
                    sender,
                    _from,
                    _tokenId,
                    _data
                )
            returns (bytes4 response) {
                if (
                    response != IERC721Receiver(_to).onERC721Received.selector
                ) {
                    revert IVotingEscrow.ERC721ReceiverRejectedTokens();
                }
            } catch (bytes memory reason) {
                if (reason.length == 0) {
                    revert IVotingEscrow
                        .ERC721TransferToNonERC721ReceiverImplementer();
                } else {
                    assembly {
                        revert(add(32, reason), mload(reason))
                    }
                }
            }
        }
    }

    function _transferFrom(
        VotingEscrowState.Storage storage self,
        address _from,
        address _to,
        uint256 _tokenId,
        address _sender
    ) internal {
        if (self.escrowType[_tokenId] == IVotingEscrow.EscrowType.LOCKED)
            revert IVotingEscrow.NotManagedOrNormalNFT();
        // Check requirements
        if (!_isApprovedOrOwner(self, _sender, _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        // Clear approval. Throws if `_from` is not the current owner
        if (_ownerOf(self, _tokenId) != _from) revert IVotingEscrow.NotOwner();
        delete self.idToApprovals[_tokenId];
        // Remove NFT. Throws if `_tokenId` is not a valid NFT
        _removeTokenFrom(self, _from, _tokenId);
        // Update voting checkpoints
        self._checkpointDelegator(_tokenId, 0, _to);
        // Add NFT
        _addTokenTo(self, _to, _tokenId);
        // Set the block of ownership transfer (for Flash NFT protection)
        self.ownershipChange[_tokenId] = block.number;
        // Log the transfer
        emit IERC721.Transfer(_from, _to, _tokenId);
    }

    function _isContract(address account) internal view returns (bool) {
        // This method relies on extcodesize, which returns 0 for contracts in
        // construction, since the code is only stored at the end of the
        // constructor execution.
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    /// @dev Add a NFT to a given address
    ///      Throws if `_tokenId` is owned by someone.
    function _addTokenTo(
        VotingEscrowState.Storage storage self,
        address _to,
        uint256 _tokenId
    ) internal {
        // Throws if `_tokenId` is owned by someone
        assert(_ownerOf(self, _tokenId) == address(0));
        // Change the owner
        self.idToOwner[_tokenId] = _to;
        // Update owner token index tracking
        _addTokenToOwnerList(self, _to, _tokenId);
        // Change count tracking
        self.ownerToNFTokenCount[_to] += 1;
    }

    /// @dev Function to mint tokens
    ///      Throws if `_to` is zero address.
    ///      Throws if `_tokenId` is owned by someone.
    /// @param _to The address that will receive the minted tokens.
    /// @param _tokenId The token id to mint.
    /// @return A boolean that indicates if the operation was successful.
    function _mint(
        VotingEscrowState.Storage storage self,
        address _to,
        uint256 _tokenId
    ) internal returns (bool) {
        // Throws if `_to` is zero address
        assert(_to != address(0));
        // Add NFT. Throws if `_tokenId` is owned by someone
        _addTokenTo(self, _to, _tokenId);
        // Update voting checkpoints
        self._checkpointDelegator(_tokenId, 0, _to);
        emit IERC721.Transfer(address(0), _to, _tokenId);
        return true;
    }

    /// @dev Add a NFT to an index mapping to a given address
    /// @param _to address of the receiver
    /// @param _tokenId uint ID Of the token to be added
    function _addTokenToOwnerList(
        VotingEscrowState.Storage storage self,
        address _to,
        uint256 _tokenId
    ) internal {
        uint256 currentCount = self.ownerToNFTokenCount[_to];

        self.ownerToNFTokenIdList[_to][currentCount] = _tokenId;
        self.tokenToOwnerIndex[_tokenId] = currentCount;
    }

    function _ownerOf(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) internal view returns (address) {
        return self.idToOwner[_tokenId];
    }

    function _isApprovedOrOwner(
        VotingEscrowState.Storage storage self,
        address _spender,
        uint256 _tokenId
    ) internal view returns (bool) {
        address owner = _ownerOf(self, _tokenId);
        bool spenderIsOwner = owner == _spender;
        bool spenderIsApproved = _spender == self.idToApprovals[_tokenId];
        bool spenderIsApprovedForAll = (self.ownerToOperators[owner])[_spender];
        return spenderIsOwner || spenderIsApproved || spenderIsApprovedForAll;
    }

    /// @dev Must be called prior to updating `LockedBalance`
    function _burn(
        VotingEscrowState.Storage storage self,
        uint256 _tokenId
    ) internal {
        address sender = self._msgSender();
        if (!_isApprovedOrOwner(self, sender, _tokenId))
            revert IVotingEscrow.NotApprovedOrOwner();
        address owner = _ownerOf(self, _tokenId);

        // Clear approval
        delete self.idToApprovals[_tokenId];
        // Update voting checkpoints
        self._checkpointDelegator(_tokenId, 0, address(0));
        // Remove token
        _removeTokenFrom(self, owner, _tokenId);
        emit IERC721.Transfer(owner, address(0), _tokenId);
    }

    /// @dev Remove a NFT from a given address
    ///      Throws if `_from` is not the current owner.
    function _removeTokenFrom(
        VotingEscrowState.Storage storage self,
        address _from,
        uint256 _tokenId
    ) internal {
        // Throws if `_from` is not the current owner
        assert(_ownerOf(self, _tokenId) == _from);
        // Change the owner
        self.idToOwner[_tokenId] = address(0);
        // Update owner token index tracking
        _removeTokenFromOwnerList(self, _from, _tokenId);
        // Change count tracking
        self.ownerToNFTokenCount[_from] -= 1;
    }

    /// @dev Remove a NFT from an index mapping to a given address
    /// @param _from address of the sender
    /// @param _tokenId uint ID Of the token to be removed
    function _removeTokenFromOwnerList(
        VotingEscrowState.Storage storage self,
        address _from,
        uint256 _tokenId
    ) internal {
        // Delete
        uint256 currentCount = self.ownerToNFTokenCount[_from] - 1;
        uint256 currentIndex = self.tokenToOwnerIndex[_tokenId];

        if (currentCount == currentIndex) {
            // update ownerToNFTokenIdList
            self.ownerToNFTokenIdList[_from][currentCount] = 0;
            // update tokenToOwnerIndex
            self.tokenToOwnerIndex[_tokenId] = 0;
        } else {
            uint256 lastTokenId = self.ownerToNFTokenIdList[_from][
                currentCount
            ];

            // Add
            // update ownerToNFTokenIdList
            self.ownerToNFTokenIdList[_from][currentIndex] = lastTokenId;
            // update tokenToOwnerIndex
            self.tokenToOwnerIndex[lastTokenId] = currentIndex;

            // Delete
            // update ownerToNFTokenIdList
            self.ownerToNFTokenIdList[_from][currentCount] = 0;
            // update tokenToOwnerIndex
            self.tokenToOwnerIndex[_tokenId] = 0;
        }
    }
}

